import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {

  const [notes, setNotes] = useState([]);

function AddNote(note) 
{
  setNotes(preNotes => {
    return [...preNotes, note];
  });
}

function deleteNote(id) 
{
  setNotes(preNotes => {
    return preNotes.filter(function(note, index) {return index !== id});
  });
}

  return (
    <div>
      <Header />
      <CreateArea onAdd={AddNote} />
      {notes.map((note, index) => {return <Note onChecked = {deleteNote} key={index} id={index} title={note.title} content={note.content} />})}
      <Footer />
    </div>
  );
}

export default App;
